		fileok=fileok && (candidato[resto++]=='.');
		fileok=fileok && (candidato[resto++]=='z');
		fileok=fileok && (candidato[resto++]=='p');
		fileok=fileok && (candidato[resto++]=='a');
		fileok=fileok && (candidato[resto++]=='q');
