bool iswinroot(string i_path)
{
#ifdef _WIN32
	/// 0123456
	/// E:        2
	/// E:\       3
	/// E:\*      4
 	/// E:\*.*    6
	/// 012345
	if (flagdebug)
		myprintf("54362: iswinroot %s\n",i_path.c_str());
	if ((i_path.size()>6) || (i_path.size()<2) || (i_path.size()==5))
		return false;
	if (flagdebug)
		myprintf("x1\n");
	if (!isalpha(i_path[0]))
		return false;
	if (flagdebug)
		myprintf("x2\n");
	if (i_path[1]!=':')
		return false;
	if (flagdebug)
		myprintf("x3\n");
	if (i_path.size()==2)
		return true;		// E:
	if (flagdebug)
		myprintf("x4\n");
	if ((i_path[2]!='/') && (i_path[2]!='\\'))
		return false;
	if (flagdebug)
		myprintf("x5\n");
	if (i_path.size()==3)
		return	true;		// E:\ E:/
	if (flagdebug)
		myprintf("x6\n");
	if ((i_path[3]!='*'))
		return false;
	if (flagdebug)
		myprintf("x7\n");
	if (i_path.size()==4)
		return true;		// E:\*, E:/*
	if (flagdebug)
		myprintf("x8\n");
	if ((i_path[4]!='.'))
		return false;
	if (flagdebug)
		myprintf("x9\n");
	if (i_path.size()==6)
		if (i_path[5]=='*')
			return true;
	if (flagdebug)
		myprintf("x10\n");
#endif
	return false;
}


