int Jidac::versum_hashdeep(FILE* i_thefile,
vector<string>& 		o_files,
vector<string>& 		o_filealgo,
vector<string>& 		o_filehash,
vector<int64_t>& 		o_filesize,
int64_t&				o_expected_size,
int&					o_expected_file)
{
	o_expected_size=0;
	o_expected_file=0;

	if (i_thefile==NULL)
	{
		myprintf("86494: file handle is null!\n");
		return 2;
	}


	/// hardcoded file format
	char line[65536];
	string linea="";
	if (!fgets(line, sizeof(line), i_thefile))
	{
		myprintf("60359: failed reading first line\n");
		return 2;
	}
	linea=line;
	if (linea.size()==0)
	{
		myprintf("60364: first line empty\n");
		return 2;
	}
	if (!fgets(line, sizeof(line), i_thefile))
	{
		myprintf("60370: failed reading second line\n");
		return 2;
	}
	linea=line;
	if (linea.size()==0)
	{
		myprintf("60371: second line empty\n");
		return 2;
	}
	if (linea.size()<6)
	{
		myprintf("60383: second line too short %d\n",linea.size());
		return 2;
	}
	if (linea[0]!='%')
	{
		myprintf("60372: second line char 0 not %%\n");
		return 2;
	}
	if (linea[1]!='%')
	{
		myprintf("60372: second line char 1 not %%\n");
		return 2;
	}
	if (linea[2]!='%')
	{
		myprintf("60372: second line char 2 not %%\n");
		return 2;
	}
	if (linea[3]!='%')
	{
		myprintf("60372: second line char 3 not %%\n");
		return 2;
	}
	if (linea[4]!=' ')
	{
		myprintf("60372: second line char 4 not space\n");
		return 2;
	}

	myreplace(linea,"%%%% ","");
	myreplace(linea,"\r","");
	myreplace(linea,"\n","");
	vector<string> pezzi;
	explode(linea,',',pezzi);
	if (pezzi.size()!=3)
	{
		myprintf("60417: second line MUST be of 3 fields (%d here)\n",pezzi.size());
		return 2;
	}
	if (pezzi[0]!="size")
	{
		myprintf("60442: first field is not size |%s|\n",pezzi[0].c_str());
		return 2;
	}
	if (pezzi[2]!="filename")
	{
		myprintf("60442: third field is not filename |%s|\n",pezzi[2].c_str());
		return 2;
	}
	if (pezzi[1]=="tiger")
	{
		myprintf("60440: sorry, tiger hash not supported\n");
		return 2;
	}
	if 	(
		(pezzi[1]!="md5") &&
		(pezzi[1]!="sha-1") &&
		(pezzi[1]!="sha1") &&
		(pezzi[1]!="sha256") &&
		(pezzi[1]!="sha-256") &&
		(pezzi[1]!="whirlpool")
		)
	{
		myprintf("60453: sorry, algo must be md5|sha1|sha256|whirlpool\n");
		return 2;
	}
	myprintf("60451: Hasher selected: %s\n",pezzi[1].c_str());

	while (fgets(line, sizeof(line), i_thefile))
	{
		linea=line;
		if (flagdebug)
			myprintf("63087: <<%s>>\n",linea.c_str());
		if (linea.size()>0)
			if  (linea[0]!='#')
			{
				string algo		="";
				string hasho	="";
				string sizo		="";
				int64_t isizo	=0;
				string nomeo	="";
				unsigned int 	j=0;
				while (j<linea.size())
				{
					if (isdigit(linea[j]))
						sizo+=linea[j];
					else
						break;
					j++;
				}

				isizo=atoll(sizo.c_str());
				j++;

				while (j<linea.size())
				{
					if (isxdigit(linea[j]))
						hasho+=linea[j];
					else
						break;
					j++;
				}

				j++;
				while (j<linea.size())
					if ((linea[j]!='\r') && (linea[j]!='\n'))
						nomeo+=linea[j++];
					else
						break;

				algo	=pezzi[1];
				hasho	=stringtoupper(hasho);
				if (flagdebug)
				{
					myprintf("63127: myfilesize %d\n",isizo);
					myprintf("63127: myfilealgo %s\n",algo.c_str());
					myprintf("63127: myfilehash %s\n",hasho.c_str());
					myprintf("63127: myfiles    %s\n",nomeo.c_str());
				}
				o_filesize	.push_back(isizo);
				o_filealgo	.push_back(algo);
				o_filehash	.push_back(hasho);
				o_files		.push_back(nomeo);
				if (flagdebug)
					myprintf("63135: Pushed\n");
				o_expected_size+=isizo;
				o_expected_file++;
			}
	}
	if (flagdebug)
		myprintf("63141: Out of while\n");
	return 0;
}

int Jidac::versum_zpaqfranz(FILE* i_thefile,
vector<string>& 		o_files,
vector<string>& 		o_filealgo,
vector<string>& 		o_filehash,
vector<int64_t>& 		o_filesize,
int64_t&				o_expected_size,
int&					o_expected_file)
{
	o_expected_size=0;
	o_expected_file=0;

	if (i_thefile==NULL)
	{
		myprintf("86703: file handle is null!\n");
		return 2;
	}

	char line[65536];

	while (fgets(line, sizeof(line), i_thefile))
	{
		string linea=line;
		if (linea.size()==0)
		{
			myprintf("86735: linea.size==0\n");
			return 2;
		}

		if (linea[0]!='|')
		{
			myprintf("86741: linea[0] != |\n");
			return 2;
		}
		string algo		="";
		string hasho	="";
		string sizo		="";
		int64_t isizo	=0;
		string nomeo	="";
		unsigned int j=1;
		while (j<linea.size())
		{
			if (linea[j]==':')
				break;
			algo+=linea[j];
			j++;
		}
		if (algo=="")
		{
			myprintf("86761: algo is empty\n");
			return 2;
		}
		j+=2;
		while (j<linea.size())
		{
			if (linea[j]==' ')
				break;
			hasho+=linea[j];
			j++;
		}
		j+=2;
		while (j<linea.size())
		{
			if (linea[j]==']')
				break;
			if (isdigit(linea[j]))
				sizo+=linea[j];
			j++;
		}
		isizo=atoll(sizo.c_str());
		j+=2;
		while (j<linea.size())
			if (linea[j++]=='|')
				break;
		while (j<linea.size())
			if ((linea[j]!='\r') && (linea[j]!='\n'))
				nomeo+=linea[j++];
			else
				break;
		o_filesize	.push_back(isizo);
		o_filealgo	.push_back(algo);
		o_filehash	.push_back(hasho);
		o_files		.push_back(nomeo);
		o_expected_size+=isizo;
		o_expected_file++;
	}
	return 0;
}
int Jidac::versum_againstzpaq(vector<string> i_myfiles,vector<string> i_filealgo,vector<string> i_filehash,vector<string>& o_missing,int& o_errori,int64_t& o_total_hashed)
{
	if (tofiles.size()==0)
	{
		myprintf("86763: tofiles size ==0\n");
		return 2;
	}
	/// test against .zpaq
	archive=tofiles[0];
	string estensione=prendiestensione(archive);
	if (estensione!="zpaq")
	{
		myprintf("60342: -to must be a .zpaq archive ");
		printUTF8(archive.c_str());
		myprintf("\n");
		return 2;
	}
	if (!fileexists(archive))
	{
		myprintf("60341: archive does not exists ");
		printUTF8(archive.c_str());
		myprintf("\n");
		return 2;
	}
	tofiles.clear();
	getpasswordifempty();
	int errors=0;
	command='l';
	g_optional="versum"; //force isselected
	read_archive(NULL,archive.c_str(),&errors,1,true);/// silent AND NOW THE MAGIC ONE!
	myprintf("\n");

	myprintf("86880: Total files/fonder in zpaq %s, files in txt %s\n",migliaia(dt.size()),migliaia2(i_myfiles.size()));

	int emptyhash		=0;
	int emptyhashtype	=0;
	int mismatchalgo	=0;

	for (unsigned int i=0;i<i_myfiles.size();i++)
	{
		string	filename=wintolinuxpath(i_myfiles[i]);
		if (searchfrom!="")
			replace(filename,searchfrom,replaceto);

		DTMap::iterator p=dt.find(filename);

	if (p==dt.end())
		{
			o_missing.push_back(filename);
			continue;
		}

		string myhashtype		="";
		string myhash			="";
		string mycrc32			="";
		int64_t	myaccesstime	=0;
		int64_t mycreationtime	=0;
		bool	myisordered		=false;
		int		myversion		=0;
		franz_posix* myposix	=NULL;

		decode_franz_block(false, //not a dir
		p->second.franz_block,
		myhashtype,
		myhash,
		mycrc32,
		mycreationtime,
		myaccesstime,myisordered,myversion,myposix);

		if (myhashtype=="")
		{
			emptyhashtype++;
			if (flagverbose)
				myprintf("60432: empty hashtype %s\n",filename.c_str());
			continue;
		}

		if (myhash=="")
		{
			emptyhash++;
			if (flagverbose)
				myprintf("60430: empty hash %s\n",filename.c_str());
			continue;
		}

		if (stringtoupper(myhashtype)!=stringtoupper(i_filealgo[i]))
		{
			mismatchalgo++;
			if (flagverbose)
				myprintf("60427: mismatch algo zpaq %s txt %s\n",myhashtype.c_str(),i_filealgo[i].c_str());
			continue;
		}

		if (stringtoupper(myhash)!=stringtoupper(i_filehash[i]))
		{
			myprintf("60413: ERROR %s |zpaq=%s|txt=%s| ",myhashtype.c_str(),i_filehash[i].c_str(),myhash.c_str());
			printUTF8(filename.c_str());
			myprintf("\n");
			o_errori++;
		}
		o_total_hashed+=p->second.size;
	}
	if (emptyhashtype>0)
		myprintf("Empty hash type %9s\n",migliaia(emptyhashtype));
	if (emptyhash>0)
		myprintf("Empty hash      %9s\n",migliaia(emptyhash));
	if (mismatchalgo>0)
		myprintf("Algo mismatch   %9s\n",migliaia(mismatchalgo));
	if (emptyhashtype+emptyhash+mismatchalgo>0)
		myprintf("Cannot test     %9s\n",migliaia(emptyhashtype+emptyhash+mismatchalgo));
	return 0;
}
int Jidac::versum()
{
	if (files.size()==0)
	{
		myprintf("60120: no files selected to be compared\n");
		return 2;
	}
	/// versum z:\*.zpaq -checktxt
	if ((flagchecktxt) || (flagfasttxt))
		return fastquicktxt();

	for (unsigned int i=0;i<files.size();i++)
		if (iszpaq(files[i]))
		{
			myprintf("86505: Cannot load a zpaq (usually on .txt) ");
			printUTF8(files[i].c_str());
			myprintf("\n");
			return 2;
		}

	if (tofiles.size()>1)
	{
		myprintf("60212: -to to exactly one .zpaq\n");
		return 2;
	}
	myprintf("86511: Loading text files\n");
	int	risultato			=0;
	int64_t	total_size		=0;
	int64_t	total_hashed	=0;
	int64_t	expected_size	=0;
	int		expected_file	=0;
	int		errori			=0;
	vector<string> 			missing;
	vector<tparametrihash> 	vettoreparametrihash;
	vector<s_stringpair> 	vec;
	vector<string> 			myfiles;
	vector<string> 			myfilealgo;
	vector<string> 			myfilehash;
	vector<int64_t> 		myfilesize;
	for (unsigned int i=0;i<files.size();i++)
	{
		string filename=files[i];
		if (!fileexists(filename))
		{
			myprintf("60128: error file does not exists ");
			printUTF8(filename.c_str());
			myprintf("\n");
			risultato=2;
			continue;
		}
		if (flaghashdeep)
			myprintf("60134: Working on hashdeep  format <<");
		else
			myprintf("60134: Working on zpaqfranz format <<");

		printUTF8(filename.c_str());
		myprintf(">>\n");
		FILE* myfile = freadopen(filename.c_str());

		if (myfile==0)
		{
			myprintf("60145: error cannot open ");
			printUTF8(filename.c_str());
			myprintf("\n");
			risultato=2;
			continue;
		}

		if (flaghashdeep)
			risultato+=	versum_hashdeep	(myfile,myfiles,myfilealgo,myfilehash,myfilesize,expected_size,expected_file);
		else
			risultato+=	versum_zpaqfranz(myfile,myfiles,myfilealgo,myfilehash,myfilesize,expected_size,expected_file);

		if (myfile!=NULL)
		{
			fclose(myfile);
			myfile=NULL;
		}
	}
	myprintf("60188: Loading finished lines %s\n",migliaia(myfiles.size()));
	if (risultato>0)
	{
		myprintf("60228: abort, because some errors\n");
		return risultato;
	}
	if (myfiles.size()==0)
	{
		myprintf("60233: no files to be checked\n");
		return 1;
	}
	int64_t	starthash=mtime();

	if (tofiles.size()==1)
		risultato=versum_againstzpaq(myfiles,myfilealgo,myfilehash,missing,errori,total_hashed);
	else		//filesystem check, one or more threads
	{
		int mythreads=howmanythreads;
		if (!flagssd)
			mythreads=1;
		total_size=expected_size;
		starthash=mtime();
		if (mythreads==1)
		{
			int64_t inizio		=mtime();
			g_dimensione=0;

			myprintf("60253: Total files %s (%s)\n",migliaia(myfiles.size()),tohuman(total_size));
			for (unsigned int i=0;i<myfiles.size();i++)
			{
				string	filename=myfiles[i];
				if (searchfrom!="")
						replace(filename,searchfrom,replaceto);

				franz_do_hash dummy(myfilealgo[i]);
				if (flagdebug)
					myprintf("88001: filehash on %s\n",filename.c_str());

				string risu=dummy.filehash(filename,false,inizio,total_size);
				if (risu!="")
				{
					s_stringpair 		mypair;
					mypair.index		=i;
					mypair.first		=myfiles[i];
					mypair.second		=risu;
					vec.push_back(mypair);
					total_hashed+=dummy.o_thefilesize;
				}
				else
					missing.push_back(filename);
			}
		}
		else // multithread
		{
			tparametrihash 	myblock;
			for (int i=0;i<mythreads;i++)
			{
				myblock.tnumber		=(i%mythreads);
				myblock.inizio		=mtime();
				myblock.dimensione	=total_size;
				myblock.timestart	=0;
				myblock.timeend		=0;
				vettoreparametrihash.push_back(myblock);
			}
			for (unsigned int i=0;i<myfiles.size();i++)
			{
				string filename=myfiles[i];
				if (searchfrom!="")
					replace(filename,searchfrom,replaceto);
				vettoreparametrihash[i%mythreads].filestobehashed.push_back(filename);
				vettoreparametrihash[i%mythreads].algo.push_back(myfilealgo[i]);
				vettoreparametrihash[i%mythreads].originalindex.push_back(i);
			}
			int totfile=0;
			for (int i=0;i<mythreads;i++)
			{
				if (flagdebug)
					myprintf("Thread [%02d] files %s\n",i,migliaia(vettoreparametrihash[i].filestobehashed.size()));
				totfile+=+vettoreparametrihash[i].filestobehashed.size();
			}
			if (flagverbose)
				myprintf("Total files %s -> in threads %s\n",migliaia(myfiles.size()),migliaia2(totfile));
			int rc;
			pthread_t* threads = new pthread_t[mythreads];
			pthread_attr_t attr;
			void *status;
			pthread_attr_init(&attr);
			pthread_attr_setdetachstate(&attr, PTHREAD_CREATE_JOINABLE);
			if (!flagnoeta)
				myprintf("\n63411: Creating %d hashing thread(s)\n",mythreads);
			for(int i = 0; i < mythreads; i++ )
			{
				vettoreparametrihash[i].timestart=mtime();
				rc = pthread_create(&threads[i], &attr, scansionahash, (void*)&vettoreparametrihash[i]);
				if (rc)
				{
					myprintf("34523: Error creating thread\n");
					exit(-1);
				}
			}
			pthread_attr_destroy(&attr);

			for(int i=0;i<mythreads; i++)
			{
				rc = pthread_join(threads[i], &status);
				if (rc)
				{
					error("Unable to join\n");
					exit(-1);
				}
			}
			delete [] threads;
			for(int i=0;i<mythreads;i++)
				for (unsigned int j=0;j<vettoreparametrihash[i].filestobehashed.size();j++)
					if (vettoreparametrihash[i].o_hashcalculated[j]!="")
					{
						s_stringpair mypair;
						mypair.index=vettoreparametrihash[i].originalindex[j];
						mypair.first=vettoreparametrihash[i].filestobehashed[j];
						mypair.second=vettoreparametrihash[i].o_hashcalculated[j];
						vec.push_back(mypair);
						///myprintf("63442: sizegetted %s\n",migliaia(vettoreparametrihash[i].o_sizegetted[j]));
						total_hashed+=vettoreparametrihash[i].o_sizegetted[j];
					}
					else
						missing.push_back(vettoreparametrihash[i].filestobehashed[j]);
		}
	}
	int64_t endhash=mtime();

	printbar(' ');
	myprintf("\r");
	if (missing.size()>0)
	{
		expected_file	-=missing.size();
		errori			+=missing.size();
		myprintf("60360: we got %s missing files (in txt, not on filesystem)\n",migliaia(missing.size()));
		for (unsigned int i=0;i<missing.size();i++)
		{
			myprintf("60363: missing ");
			printUTF8(missing[i].c_str());
			myprintf("\n");
		}
	}
	printbar('-');
	myprintf("Files %21s vs exp %21s ",migliaia(expected_file),migliaia2(myfiles.size()));
	if ((unsigned int)expected_file!=myfiles.size())
	{
		myprintf(" *** DIFFER %s ***",migliaia(myabs(myfiles.size(),expected_file)));
		risultato=2;
	}
	else
		myprintf(" this is good");

	myprintf("\n");
	myprintf("Bytes %21s vs exp %21s ",migliaia(total_hashed),migliaia2(expected_size));
	if (total_hashed!=expected_size)
	{
		myprintf(" *** DIFFER %s ***",migliaia(myabs(expected_size,total_hashed)));
		risultato=2;
	}
	else
		myprintf(" this is good");

	myprintf("\n");
	printbar('-');
	std::sort(vec.begin(),vec.end(),pairsortbyindex);
	for (unsigned int i=0;i<vec.size();i++)
	{
		string vec_filename	=vec[i].first;
		string vec_hash		=vec[i].second;
		if (vec_hash!=myfilehash[vec[i].index])
		{
			myprintf("60402: ERROR %s |file=%s|txt=%s| ",myfilealgo[i].c_str(),vec_hash.c_str(),myfilehash[i].c_str());
			printUTF8(vec_filename.c_str());
			myprintf("\n");
			errori++;
		}
		else
		{
			if (flagverbose)
				myprintf("60413: hash OK in %08d  file %s  txt %s\n",vec[i].index,vec_hash.c_str(),vec_filename.c_str());
		}
	}
	if (errori>0)
		printbar('-');
	int64_t hashtime	=endhash-starthash;
	int64_t myspeed		=(int64_t)(total_hashed*1000.0/(hashtime));
	myprintf("Time  %15.3f s, average speed %s/s (%s/s)\n",hashtime/1000.0,migliaia(myspeed),tohuman(myspeed));

	if (errori==0)
		return 0;
	else
		return 2;
	return 0;
}

int Jidac::fastquicktxt()
{
	if (files.size()==0)
	{
		myprintf("87091: Files size must >1\n");
		return 2;
	}

	if ((!flagchecktxt) && (!flagfasttxt))
		if (!iswildcard(files[0]))
			if (prendiestensione(files[0])=="zpaq")
			{
				myprintf("66843: Automagically search for .txt ");
				string 	thezpaq		=files[0];
				string 	percorso	=extractfilepath		(thezpaq);
				string	nome		=prendinomefileebasta	(thezpaq);
				string 	mychecktxt;
				if (fileexists(percorso+nome+"_md5.txt"))
					flagchecktxt=true;
				if (fileexists(percorso+nome+".md5"))
					flagchecktxt=true;
				if (fileexists(percorso+nome+"_crc32.txt"))
					flagfasttxt=true;
				if (flagchecktxt || flagfasttxt)
					myprintf(": FOUNDED !\n");
				else
					myprintf(": NOTHING\n");
			}

	if (flagchecktxt && flagfasttxt)
	{
		myprintf("87118: Confused by -checktxt and -fasttxt\n");
		return 2;
	}
	unsigned int	startline	=0;
	string 			thehash		="MD5";
	unsigned int	thehashlen	=32;
	if (flagchecktxt)
	{
		myprintf("66265: Test MD5 hashes of .zpaq against _md5.txt\n");
		if (flagbackupxxh3)
			thehash="XXH3";
	}
	else
	{
		startline	=1;
		thehash		="CRC-32";
		thehashlen	=8;
		if (flagquick)
		{
			thehash		="QUICK";
			thehashlen	=8;
		}
		myprintf("66764: Test %s of .zpaq against _crc32.txt\n",thehash.c_str());
	}
	DTMap thedt;
	for (unsigned int i=0;i<files.size();i++)
		getjollylist(files[i],&thedt);

	if (thedt.size()==0)
	{
		myprintf("66282: Nothing to do (wrong file selection?)\n");
		return 0;
	}

	int64_t larghezzain			=0;
	for (DTMap::iterator p=thedt.begin(); p!=thedt.end(); ++p)
		larghezzain+=prendidimensionefile(p->first.c_str());

	myprintf("87163: Bytes to be checked %s (%s) in files %s\n",migliaia(larghezzain),tohuman(larghezzain),migliaia2(thedt.size()));

	unsigned int	testok		=0;
	unsigned int	testwarning	=0;
	unsigned int 	testerror	=0;
	int64_t 	startverify		=mtime();

	for (DTMap::iterator p=thedt.begin(); p!=thedt.end(); ++p)
	{
		string 	thezpaq		=p->first;
		string 	percorso	=extractfilepath		(thezpaq);
		string	nome		=prendinomefileebasta	(thezpaq);
		string 	mychecktxt;
		if (flagchecktxt)
		{
			mychecktxt=percorso+nome+"_md5.txt";
			if (!fileexists(mychecktxt))
				mychecktxt	=percorso+nome+".md5";
		}
		else
			mychecktxt=percorso+nome+"_crc32.txt";

		if (!fileexists(mychecktxt))
		{
			myprintf("66240: WARN for file ");
			printUTF8(thezpaq.c_str());
			myprintf(" missing ");
			printUTF8(mychecktxt.c_str());
			myprintf("\n");
			testwarning++;
			continue;
		}

		if (flagverbose)
		{
			myprintf("66240: Getting %s on ",thehash.c_str());
			printUTF8(thezpaq.c_str());
			myprintf("\n");
		}

		vector<string> checktxtlines;
		if (!readfiletoarray(mychecktxt,checktxtlines))
		{
			myprintf("66267: Guru readfiletoarray ");
			printUTF8(mychecktxt.c_str());
			myprintf("\n");
			continue;
		}

		if (checktxtlines.size()<=startline)
		{
			myprintf("66270: checktxtlines.size %d <= %d\n",checktxtlines.size(),startline);
			continue;
		}

		string	firstline	=checktxtlines[startline];
		string 	hashfromtxt	=getfirsthash(firstline);

		if (hashfromtxt.size()!=thehashlen)
		{
			myprintf("66370: hash size %d != %d %s\n",hashfromtxt.size(),thehashlen,firstline.c_str());
			continue;
		}

		if (flagfasttxt && flagquick)
		{
			firstline	=mytrim(myright(firstline,firstline.size()-8));
			firstline	=getfirsthash(firstline);
			hashfromtxt	=firstline;
		}

		franz_do_hash dummy(thehash);
		if (flagdebug)
			myprintf("88303: filehash on %s\n",thezpaq.c_str());

		string hashreloaded=dummy.filehash(thezpaq,false,startverify,larghezzain);
		printbar(' ');
		myprintf("\r");

		if (hashreloaded=="")
		{
			myprintf("66254: guru doing hash on ");
			printUTF8(thezpaq.c_str());
			myprintf("\n");
			continue;
		}
		if (flagdebug)
		{
			myprintf("66248: %s: %s %s for file ",thehash.c_str(),hashreloaded.c_str(),hashfromtxt.c_str());
			printUTF8(thezpaq.c_str());
			myprintf("\n");
		}
		myprintf("66323: ");
		if (stringtolower(hashreloaded)==stringtolower(hashfromtxt))
		{
			myprintf("OK %s: ",thehash.c_str());
			testok++;
		}
		else
		{
			myprintf("ERROR: reloaded %s vs txt %s ",hashreloaded.c_str(),hashfromtxt.c_str());
			testerror++;
		}
		printUTF8(thezpaq.c_str());
		myprintf("\n");
	}
	printbar('=');

	myprintf("66356: TOTAL  %9s\n",migliaia(thedt.size()));
	myprintf("66357: OK     %9s\n",migliaia(testok));
	myprintf("66358: WARN   %9s\n",migliaia(testwarning));
	myprintf("66359: ERROR  %9s\n",migliaia(testerror));

	if (testwarning>0)
		if (testerror==0)
			return 1;

	if (testerror>0)
		return 2;

	return 0;
}
